from minieigen import Vector2, MatrixX

def Matrix2(a,b,c,d):
	return MatrixX([a,b],[c,d])
Matrix2_Identity = Matrix2(1,0,0,1)
Matrix2_Zero = Matrix2(0,0,0,0)

PLOTTEXT = True
