r = 60
rc = 5
cs = (
	Vector2(105,140),
	Vector2(205,240),
	Vector2(290,110),
)
for i,ir in enumerate((1.0,1.2,1.4,1.6)):
	paper = Paper("interaction_ratio_{}".format(i),400,345)
	c0 = cs[0]
	x0,y0 = c0
	for x,y in cs:
		paper.circle(x,y,r).fill(gray)
		paper.circle(x,y,rc).stroke(null).fill('black')
		paper.circle(x,y,r*ir).dotted()
	for ci in cs:
		for cj in cs:
			if ci == cj:
				continue
			xi,yi = ci
			xj,yj = cj
			if (ci-cj).norm() < 2*ir*r:
				paper.path(["M",xi,yi,"L",xj,yj]).mediumThick().stroke(red)
	paper.save()

