w,h = 300,50
def sleeper(paper,y):
	dy = 5
	hh = 20
	dx0,dx1,dx2 = 30,10,50
	ww = 4*dx0 + 2*dx1 + dx2
	paper.path(["M",.5*w-.5*ww,y+h-hh,"l",dx0,-dy,"h",dx1,"l",dx0,dy,"h",dx2,"l",dx0,-dy,"h",dx1,"l",dx0,dy,"v",hh-dy,"h",-ww,"Z"]).fill(dark_gray)
def ballast(paper,y):
	d = 5
	d2,d3,d4 = [i*d for i in (2,3,4)]
	y2 = y+d4
	g0 = lambda x,y: ["M",x,y,"h",d3,"l",d,d,"v",d2,"l",-d,d,"l",-d3,-d,"Z"]
	g1 = lambda x,y: ["M",x,y+d,"l",d2,-d,"l",d2,d,"v",d,"l",-d,d2,"h",-d2,"Z"]
	g2 = lambda x,y: ["M",x,y+d3,"l",d,-d3,"h",d,"l",d2,d,"l",-d,d2,"l",-d,d,"Z"]
	g3 = lambda x,y: ["M",x,y+d2,"v",-d,"l",d3,-d,"l",d,d,"v",d2,"l",-d2,d,"l",-d2,-d,"Z"]
	l0 = [g(20+i*20,y) for i,g in enumerate((g0,g1,g2,g3,g1,g3,g0,g1,g3,g2,g3,g0,g1))]
	l1 = [g(10+i*20,y+d4) for i,g in enumerate((g2,g1,g0,g3,g2,g3,g0,g1,g2,g0,g3,g1,g2,g0))]
	p = sum(l0+l1,[])
	paper.path(p).fill(light_gray)
Raphael.fn.sleeper = sleeper
Raphael.fn.ballast = ballast
