w,h = 200,150
ds1 = 50,30,30,30
ds2 = 40,30,45,30
drots1 = 0,.5*pi,pi,1.5*pi
drots2 = .25*pi,.75*pi,1.25*pi,1.75*pi
def particle(paper,x,y,rot,drots,ds):
	rots = [rot+drot for drot in drots]
	cs = [cos(r) for r in rots]
	ss = [sin(r) for r in rots]
	p = [["L",x+d*c,y+d*s] for d,c,s in zip(ds,cs,ss)]
	p = sum(p,[])
	p[0] = "M"
	p.append("Z")
	return paper.path(p).normal(), paper.circle(x,y,4).stroke(null).fill(black)
Raphael.fn.particle1 = lambda paper,x,y,rot: particle(paper,x,y,rot,drots1,ds1)
Raphael.fn.particle2 = lambda paper,x,y,rot: particle(paper,x,y,rot,drots2,ds2)
